// Type-safe translation function signature
export interface TypeSafeTranslationFunction {
  (key: TranslationKey, params?: Record<string, any>): string;
}

// Translation validation helper
export const isValidTranslationKey = (key: string): key is TranslationKey => {
  // This would be populated with all valid keys in a real implementation
  const validKeys: Set<string> = new Set([
    // Add all translation keys here for runtime validation
    'app.title',
    'nav.dashboard',
    'common.save',
    'common.cancel',
    // ... etc
  ]);
  
  return validKeys.has(key);
};

// Translation key categories for organization
export const TRANSLATION_KEY_CATEGORIES = {
  APP: 'app',
  NAV: 'nav',
  AUTH: 'auth',
  COMMON: 'common',
  DASHBOARD: 'dashboard',
  CUSTOMERS: 'customers',
  ACCOUNTING: 'accounting',
  REPORTS: 'reports',
  SMS: 'sms',
  SETTINGS: 'settings',
  FORMS: 'forms',
  CHARTS: 'charts',
  TABLES: 'table',
  SYSTEM: 'system',
  INVENTORY: 'inventory'
} as const;

export type TranslationKeyCategory = typeof TRANSLATION_KEY_CATEGORIES[keyof typeof TRANSLATION_KEY_CATEGORIES];

// Helper to get category from key
export const getTranslationKeyCategory = (key: TranslationKey): TranslationKeyCategory => {
  const prefix = key.split('.')[0];
  return prefix as TranslationKeyCategory;
};

// Helper to validate key format
export const validateTranslationKeyFormat = (key: string): boolean => {
  const keyPattern = /^[a-z][a-z0-9]*(\.[a-z][a-z0-9_]*)*$/;
  return keyPattern.test(key);
};